package com.sportsinfo.community; import jakarta.persistence.*; import lombok.*; import java.time.OffsetDateTime; @Entity @Table(name="discussions") @Getter @Setter @NoArgsConstructor @AllArgsConstructor @Builder public class Discussion { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; private String sport; private String title; @Column(length=4000) private String content; private String author; private OffsetDateTime createdAt = OffsetDateTime.now(); }
