package com.sportsinfo.user; import jakarta.persistence.*; import lombok.*; import java.util.*; @Entity @Table(name="users") @Getter @Setter @NoArgsConstructor @AllArgsConstructor @Builder public class User { @Id @GeneratedValue(strategy=GenerationType.IDENTITY) private Long id; @Column(unique=true, nullable=false) private String email; @Column(unique=true) private String phone; @Column(nullable=false) private String passwordHash; private String name; @Enumerated(EnumType.STRING) private Role role = Role.USER; private String theme = "light"; @OneToMany(mappedBy="user", cascade=CascadeType.ALL, orphanRemoval=true, fetch=FetchType.LAZY) private Set<Favorite> favorites = new HashSet<>(); }
